import React,{useState,useEffect} from 'react'
import { Accordion, Container,Col,Card,Button } from 'react-bootstrap';
import { useSelector } from 'react-redux';
import { Redirect } from 'react-router-dom';
import Navb from '../components/navb';
import axios from "axios";


function Asignatura() {
    const isLogged = useSelector((store) => store.authReducer.isLogged);
    const id_asignatura= useSelector((store) => store.authReducer.id_asignatura);
    const name= useSelector((store) => store.authReducer.Name);
    const tipo= useSelector((store) => store.authReducer.tipo);
    const [archivos, setArchivos] = useState([]);
    const instance = axios.create({
        baseURL: 'http://localhost:3000/asignatura'
      });


    useEffect(() => {
        const fetchData = async () => {
          try {
            const response = await instance.post("/",{
                idAsignatura:id_asignatura
            });
            
            
            setArchivos(response.data);
          } catch (err) {}
        };
    
        fetchData();
      }, []);
    return isLogged?(
        <Container fluid>
            <Navb name={name} tipo={tipo}></Navb>
            <h1 style={{textAlign:'center'}}>{archivos[0].Asignatura.nombre}</h1>
            
            <Container>

                <Accordion>
                {archivos.map(arch =>{if(true){return(
                    <Card>
                    <Card.Header>
                    <Accordion.Toggle as={Button} variant="link" eventKey="0">
                        Click me!
                    </Accordion.Toggle>
                    </Card.Header>
                    <Accordion.Collapse eventKey="0">
                    <Card.Body>Hello! I'm the body</Card.Body>
                    </Accordion.Collapse>
                </Card>


                )}return null})}
                
                
                ID de asignatura : {id_asignatura} 
                </Accordion>
            </Container>
            
        </Container>
            
    ):<Redirect to="/home"/>
}

export default Asignatura
