import React,{useState,useEffect} from 'react'
import axios from "axios";
import Navb from '../components/navb';
import { Container, Form, ListGroup } from 'react-bootstrap';
import {useSelector} from 'react-redux';
import { Redirect } from 'react-router-dom';

const dotenv = require("dotenv");
dotenv.config();
function Mensaje() {
    const isLogged = useSelector((store) => store.authReducer.isLogged);
    const name = useSelector((store) => store.authReducer.Name);
    const tipo= useSelector((store) => store.authReducer.tipo);
    const rut= useSelector((store) => store.authReducer.Rut);
    const [curso,setCurso] = useState([])
    const [alumnos,setAlumnos] = useState([])
    const instance = axios.create({
        baseURL: process.env.REACT_APP_BACKEND_URL+'/mensaje'
      });
    
    useEffect(() => {
        const fetchData = async () => {
        if(tipo==="Profesor"){
          try {
            const response = await instance.post("/cosa",{
                rut:rut
            });
            
            setCurso(response.data[0].Curso.id);
            if(curso){
                try {
                    const resp = await instance.post("/alumnos",{
                        idCurso:curso
                    });
                    setAlumnos(resp.data)
                    
                } catch (error) {
                    
                }
            }
            
          } catch (err) {console.log(err)}
        };
        }
    
        fetchData();
      }, [curso]);
      
    return isLogged ?(
        <Container fluid>
        <Container fluid>
            <Navb name={name} tipo={tipo}></Navb>
            
        </Container>
        <Container>
        <ListGroup>{alumnos && alumnos.map(alumno =>{return(<ListGroup.Item key={alumno.rut}>{alumno.rut}</ListGroup.Item>)})}</ListGroup>
            <Form>
            <Form.Label>Destinatario</Form.Label>
                        <Form.Control as="select">
                            <option value={null}>Escoger Destinatario</option>
                            {alumnos && alumnos.map(alumno =>{return(<option value={alumno.rut} key={alumno.rut}>{alumno.rut}</option>)})}
                                                
                        </Form.Control>
            </Form>
        </Container>
        </Container>
    ):(
        <Redirect to="/login"/>
    )
}

export default Mensaje
