import React,{useState,useEffect} from 'react'
import axios from "axios";
import Navb from '../components/navb';
import { Container, Form, Modal,Button, Card, Accordion } from 'react-bootstrap';
import {useSelector} from 'react-redux';
import { Redirect } from 'react-router-dom';

const dotenv = require("dotenv");
dotenv.config();
function Mensaje() {
    const isLogged = useSelector((store) => store.authReducer.isLogged);
    const name = useSelector((store) => store.authReducer.Name);
    const tipo= useSelector((store) => store.authReducer.tipo);
    const rut= useSelector((store) => store.authReducer.Rut);
    const [curso,setCurso] = useState([])
    const [show, setShow] = useState(false);
    const [mensaje,setMensaje] = useState([])
    const [alumnos,setAlumnos] = useState([])
    const [dest,setDest] = useState([])
    const [dest1,setDest1] = useState([])
    const [contenido,setContenido] = useState("")
    var crs =alumnos && alumnos.map(alumno =>{
        return alumno.rut
    })
    var crs1 =alumnos && alumnos.map(alumno =>{
        return alumno.Apoderado.rut
    })
    var crs2 = crs.concat(crs1)
    const instance = axios.create({
        baseURL: process.env.REACT_APP_BACKEND_URL+'/mensaje'
      });
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const handleSelectDest = (e) =>{
        if(e.target.value==="Alumnos"){
            setDest1(crs)
            setDest([])
        }
        else if(e.target.value==="Apoderados"){
            setDest1(crs1)
            setDest([])
        }
        else if(e.target.value==="Curso"){
            setDest1(crs2)
            setDest([])
        }
        else{
        setDest([e.target.value])
        }
    }
    
    const handleSelectDest1 = (e) =>{
        
        setDest1([e.target.value])
        
    }
    const handleContenido = (e) =>{
        setContenido(e.target.value)
        
    }
    const handleSubmit = async (e)=>{
        try {
            await instance.post("/enviar",{
                idSender:rut,
                idReceiver:dest1,
                contenido:contenido,
                

            })
            handleClose()
        } catch (error) {
            console.log(error)
        }
        
    }
    const renderDest = () => {
        if (dest[0]==="Al") {
          return (<div><Form.Label>Destinatario</Form.Label>
            <Form.Control as="select" onChange={handleSelectDest1}>
                <option value={null}>Escoger Destinatario</option>
                {alumnos && alumnos.map(alumno =>{return(<option value={alumno.rut} key={alumno.rut}>{alumno.rut}/{alumno.nombre}</option>)})}                       
            </Form.Control></div>);
        } else if(dest[0]==="Ap") {
          return (<div><Form.Label>Destinatario</Form.Label>
            <Form.Control as="select" onChange={handleSelectDest1}>
                <option value={null}>Escoger Destinatario</option>
                {alumnos && alumnos.map(alumno =>{return(<option value={alumno.Apoderado.rut} key={alumno.Apoderado.rut}>{alumno.Apoderado.rut}/{alumno.Apoderado.nombre}</option>)})}                       
            </Form.Control></div>);
        }
      }
    
    useEffect(() => {
        const fetchData = async () => {
        if(tipo==="Profesor"){
          try {
            const response = await instance.post("/cosa",{
                rut:rut
            });
            setCurso(response.data[0].Curso.id);
            if(curso){
                try {
                    const resp = await instance.post("/alumnos",{
                        idCurso:curso
                    });
                    setAlumnos(resp.data)
                    
                } catch (error) {
                    
                }
            }
            
          } catch (err) {console.log(err)}
        }
        else{
            try {
                const mnsj= await instance.post("/m",{
                    rut:rut
                });
                setMensaje(mnsj.data)
                
            } catch (error) {
                
            }
        }
        }
    
        fetchData();
      }, [curso,dest]);
    
    return isLogged ?(
        (()=>{
            switch(tipo){
                case "Profesor":
                    return(
                        
                        <Container fluid>
                            
                            
                            <Navb name={name} tipo={tipo}></Navb>
                            
                            
                        <Container>
                                <h1>MENSAJES RECIBIDOS</h1>
                                <Accordion>
                                    {mensaje && mensaje.map(msj =>{return(<Card key={msj.id}>
                                                                            <Accordion.Toggle as={Card.Header}  eventKey={msj.id}>
                                                                            Click me! {msj.idSender}
                                                                            </Accordion.Toggle>
                                                                            <Accordion.Collapse eventKey={msj.id}>
                                                                            <Card.Body>{msj.contenido}</Card.Body>
                                                                            
                                                                            </Accordion.Collapse>
                                                                        </Card>)})}
                                </Accordion>
                            </Container>
                        <Container>
                        <Button variant="primary" onClick={handleShow}>
                            Launch demo modal
                        </Button>
                            
                        </Container>
                        
                        <Modal show={show} onHide={handleClose}>
                            <Modal.Header closeButton>
                            <Modal.Title>Modal heading</Modal.Title>
                            </Modal.Header>
                            <Modal.Body>
                                <Form>
                            <Form.Label>Tipo Destinatario</Form.Label>
                                        <Form.Control as="select" onChange={handleSelectDest}>
                                            <option value={null}>Escoger Destinatario</option>
                                            <option value={"Curso"}>Curso</option>
                                            <option value={"Alumnos"}>Alumnos</option>
                                            <option value={"Apoderados"}>Apoderados</option>
                                            <option value={"Al"}>Alumno Especifico</option>
                                            <option value={"Ap"}>Apoderado Especifico</option>                      
                                        </Form.Control>
                            {renderDest()}
                            <Form.Label>Mensaje</Form.Label>
                                <Form.Control as="textarea" rows={5} onChange={handleContenido} />
                            <br/>
                            <Button variant="secondary" onClick={handleClose}>
                                Close
                            </Button>
                            <Button style = {{marginLeft:"300px"}}type="submit" onClick={handleSubmit}>Enviar</Button>
                            </Form>

                            </Modal.Body>
                            
                        </Modal>

                        </Container>
                        )
                case "Estudiante":
                    return(<Container fluid>
                            <Navb name={name} tipo={tipo}></Navb>
                            <Container>
                                <h1>MENSAJES RECIBIDOS</h1>
                                <Accordion>
                                    {mensaje && mensaje.map(msj =>{return(<Card key={msj.id}>
                                                                            <Accordion.Toggle as={Card.Header}  eventKey={msj.id}>
                                                                            Click me! {msj.idSender}
                                                                            </Accordion.Toggle>
                                                                            <Accordion.Collapse eventKey={msj.id}>
                                                                            <Card.Body>{msj.contenido}</Card.Body>
                                                                            
                                                                            </Accordion.Collapse>
                                                                        </Card>)})}
                                </Accordion>
                            </Container>
                        
                            </Container>)
                case "Apoderado":
                    return(<Container fluid>
                            <Navb name={name} tipo={tipo}></Navb>
                            <Container>
                                <h1>MENSAJES RECIBIDOS</h1>
                                <Accordion>
                                    {mensaje && mensaje.map(msj =>{return(<Card key={msj.id}>
                                                                            <Accordion.Toggle as={Card.Header}  eventKey={msj.id}>
                                                                            Click me! {msj.idSender}
                                                                            </Accordion.Toggle>
                                                                            <Accordion.Collapse eventKey={msj.id}>
                                                                            <Card.Body>{msj.contenido}</Card.Body>
                                                                            
                                                                            </Accordion.Collapse>
                                                                        </Card>)})}
                                </Accordion>
                            </Container>
                        
                            </Container>)
                default:
                    return(<div>hola</div>)
            }
        
        })()
    ):(
        <Redirect to="/login"/>
    )
}

export default Mensaje
