import React,{useEffect,useState} from 'react';
import { Table } from 'react-bootstrap';
import axios from 'axios';

const Horario = (props) => {
    let {idCurso} = props;
    const[asignaturas,setAsignaturas]=useState([])
    const[bloque1,setBloque1]=useState([])
    const[bloque2,setBloque2]=useState([])
    const[bloque3,setBloque3]=useState([])
    const[bloque4,setBloque4]=useState([])
    const instance = axios.create({
        baseURL: 'http://localhost:3000/home'
      });
    useEffect(() => {
        const fetchData = async () => {
          try {
            const response = await instance.post("/asignaturas",{
                id:idCurso,
            });
            setAsignaturas(response.data);
            const b1=(asignaturas.filter(asignaturas=>asignaturas["bloque"]==1))
            setBloque1(b1)
            console.log(bloque1[0].id)
            const bloque2 = asignaturas.filter(asignaturas=>asignaturas["bloque"]==2)
            const bloque3 = asignaturas.filter(asignaturas=>asignaturas["bloque"]==3)
            const bloque4 = asignaturas.filter(asignaturas=>asignaturas["bloque"]==4)
            const bloque5 = asignaturas.filter(asignaturas=>asignaturas["bloque"]==5)
            const bloque6 = asignaturas.filter(asignaturas=>asignaturas["bloque"]==6)
            //bloque1.map(asignatura =>{if(asignatura.dia=="Viernes"){
            //    console.log(asignatura)
            //}})
            
            
          } catch (err) {}
        };
        fetchData();
      }, []);
    return (
        <Table striped bordered hover>
            <thead>
                <tr>
                <th>Bloque</th>
                <th>Lunes</th>
                <th>Martes</th>
                <th>Miercoles</th>
                <th>Jueves</th>
                <th>Viernes</th>
                </tr>
            </thead>
                <tr>
                <td>1</td>
                <td>{bloque1.map(asignatura =>{if(asignatura.dia==="Lunes"){return asignatura.Asignatura.nombre}})}</td>
                <td>{bloque1.map(asignatura =>{if(asignatura.dia==="Martes"){return asignatura.Asignatura.nombre}})}</td>
                <td>{bloque1.map(asignatura =>{if(asignatura.dia==="Miercoles"){return asignatura.Asignatura.nombre}})}</td>
                <td>{bloque1.map(asignatura =>{if(asignatura.dia==="Jueves"){return asignatura.Asignatura.nombre}})}</td>
                <td>{bloque1.map(asignatura =>{if(asignatura.dia==="Viernes"){return asignatura.Asignatura.nombre}})}</td>
                </tr>
            <tbody>

            </tbody>
        </Table>
    )
};

export default Horario;
