import React, {useState,useEffect} from 'react'
import {Container,Row,Col,Form,InputGroup,Button,Dropdown,DropdownButton} from 'react-bootstrap'
import axios from "axios";

function Admin() {
    const instance = axios.create({
        baseURL: 'http://localhost:3000/admin'
      });
    const [selected,setSelected]=useState('Usuario')
    const [cursos,setCursos]=useState([])
    const [asignaturas,setAsignaturas]=useState([])
    const [estudiantes,setEstudiantes]=useState([])
    const [rut,setRut]=useState("");
    const [pass,setPass]=useState("");
    const [name,setName]=useState("");
    const [selectedCurso,setSelectedCurso]=useState("");
    const [selectedRegistrado,setSelectedRegistrado]=useState();
    const handleSelect = (e) =>{
        setSelected(e)
    }
    const handleSelectCurso = (e) =>{
        setSelectedCurso(e)
    }
    const handleSelectRegistrado = (e) =>{
        setSelectedRegistrado(e)
    }
    const handleRut = (e) =>{
        setRut(e)
    }
    const handleName = (e) =>{
        setName(e)
    }
    const handlePass = (e) =>{
        setPass(e)
    }

    useEffect(() => {
        const fetchData = async () => {
          try {
            const response = await instance.get("/");
            const response1 = await instance.get("/a");
            const response2 = await instance.get("/e");
            setCursos(response.data);
            setAsignaturas(response1.data);
            setEstudiantes(response2.data);
          } catch (err) {}
        };
    
        fetchData();
      }, []);
      const handleSumbit = async (e) =>{
        e.preventDefault()
        switch(selected){
            case "Estudiante":
                try {
                    await instance.post("/estudiante",{
                        rut :rut,
                        nombre:name,
                        password: pass,
                        idCurso: selectedCurso,
                        registrado: selectedRegistrado
                    });
                    
                } catch (error) {
                    
                }
                break;
    }
    }
    return (
        <div>
            <Container>
            
            <Row>
                <Col>
                <Form>
                    <Form.Group controlId="formBasicPassword">
                        <Form.Label>Rut</Form.Label>
                        <InputGroup className="mb-3">
                            <Form.Control  onChange={handleRut}/>
                            <DropdownButton
                            as={InputGroup.Append}
                            variant="outline-secondary"
                            title={selected}
                            id="input-group-dropdown-1"
                            onSelect={handleSelect}
                            
                            >
                            <Dropdown.Item eventKey="Estudiante">Estudiante</Dropdown.Item>
                            <Dropdown.Item eventKey="Profesor">Profesor</Dropdown.Item>
                            <Dropdown.Item eventKey="Apoderado">Apoderado</Dropdown.Item>
                            </DropdownButton>
                            
                        </InputGroup>
                    
                    </Form.Group>
                    
                    
                    
                    {(() => {
                        switch (selected) {
                            case 'Estudiante':
                            return(<div>
                                <Form.Group controlId="formBasicPassword">
                                    <Form.Label>Nombre</Form.Label>
                                    <Form.Control onChange={handleName} />
                                </Form.Group>
                                <Form.Group controlId="formBasicPassword">
                                    <Form.Label>Contraseña</Form.Label>
                                    <Form.Control onChange={handlePass} />
                                </Form.Group>
                                <Form.Group>
                                <Form.Label>Curso</Form.Label>
                                <Form.Control as="select" onSelect={handleSelectCurso}>
                                    
                                    {cursos.map(curso =>{
                                        return(
                                            <option key={curso.id}>{curso.grado}</option>
                                        )
                                    })}
                                    
                                </Form.Control>
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>Registrado</Form.Label>
                                    <Form.Control as ="select" onSelect={handleSelectRegistrado}>
                                        <option>Mantener</option>
                                        <option key={true}>true</option>
                                        <option key={false}>false</option>
                                        </Form.Control>
                                </Form.Group>
                                <br/>
                                <Button  variant="primary" type="submit" onClick={handleSumbit}>
                                    Add/Update
                                </Button>
                                <Button  variant="danger" type="submit">
                                    Delete
                                </Button>
                                </div>)
                                case 'Profesor':
                                    return(<div>
