import React, {useState,useEffect} from 'react'
import {Container,Row,Col,Form,InputGroup,Button,Dropdown,DropdownButton} from 'react-bootstrap'
import axios from "axios";

function Admin() {
    const instance = axios.create({
        baseURL: 'http://localhost:3000/admin'
      });
    const [selected,setSelected]=useState('Usuario')
    const [cursos,setCursos]=useState([])
    const [asignaturas,setAsignaturas]=useState([])
    const handleSelect = (e) =>{
        setSelected(e)
    }
    useEffect(() => {
        const fetchData = async () => {
          try {
            const response = await instance.get("/");
            const response1 = await instance.get("/a");
            setCursos(response.data);
            setAsignaturas(response1.data);
          } catch (err) {}
        };
    
        fetchData();
      }, []);
    return (
        <div>
            <Container>
            
            <Row>
                <Col>
                <Form>
                    <Form.Group controlId="formBasicPassword">
                        <Form.Label>Rut</Form.Label>
                        <InputGroup className="mb-3">
                            <Form.Control  />
                            <DropdownButton
                            as={InputGroup.Append}
                            variant="outline-secondary"
                            title={selected}
                            id="input-group-dropdown-1"
                            onSelect={handleSelect}
                            
                            >
                            <Dropdown.Item eventKey="Estudiante">Estudiante</Dropdown.Item>
                            <Dropdown.Item eventKey="Profesor">Profesor</Dropdown.Item>
                            <Dropdown.Item eventKey="Apoderado">Apoderado</Dropdown.Item>
                            </DropdownButton>
                            
                        </InputGroup>
                    
                    </Form.Group>
                    
                    
                    
                    {(() => {
                        switch (selected) {
                            case 'Estudiante':
                            return(<div>
                                <Form.Group controlId="formBasicPassword">
                                    <Form.Label>Contraseña</Form.Label>
                                    <Form.Control  />
                                </Form.Group>
                                <Form.Group>
                                <Form.Label>Curso</Form.Label>
                                <Form.Control as="select">
                                    
                                    {cursos.map(curso =>{
                                        return(
                                            <option key={curso.id}>{curso.grado}</option>
                                        )
                                    })}
                                    
                                </Form.Control>
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>Registrado</Form.Label>
                                    <Form.Control as ="select">
                                        <option>Mantener</option>
                                        <option>true</option>
                                        <option>false</option>
                                        </Form.Control>
                                </Form.Group>
                                <br/>
                                <Button  variant="primary" type="submit">
                                    Add/Update
                                </Button>
                                <Button  variant="danger" type="submit">
                                    Delete
                                </Button>
                                </div>)
                                case 'Profesor':
                                    return(<div>
                                                    <Form.Group controlId="formBasicPassword">
                                                <Form.Label>Contraseña</Form.Label>
                                                <Form.Control  />
                                            </Form.Group>
                                            <Form.Group>
                                            <Form.Label>Asignatura</Form.Label>
                                            <Form.Control as="select">
                                                
                                                {asignaturas.map(asignatura =>{
                                                    return(
                                                        <option key={asignatura.id}>{asignatura.nombre}</option>
                                                    )
                                                })}
                                                
                                            </Form.Control>
                                            </Form.Group>
                                            <Form.Group>
                                            <Form.Label>Curso</Form.Label>
                                            <Form.Control as="select">
                                                
                                                {cursos.map(curso =>{
                                                    return(
                                                        <option key={curso.id}>{curso.grado}</option>
                                                    )
                                                })}
                                                
                                            </Form.Control>
                                            </Form.Group>
                                            <Form.Group>
                                                <Form.Label>Registrado</Form.Label>
                                                <Form.Control as ="select">
                                                    <option>Mantener</option>
                                                    <option>true</option>
                                                    <option>false</option>
                                                    </Form.Control>
                                            </Form.Group>
                                            
                                            <Form.Group>
                                                <Form.Label>Profesor Jefe</Form.Label>
                                                <Form.Control as ="select">
                                                    <option>Mantener</option>
                                                    <option>true</option>
                                                    <option>false</option>
                                                    </Form.Control>
                                            </Form.Group>
                                            <br/>
                                            <Button  variant="primary" type="submit">
                                                Add/Update
                                            </Button>
                                            <Button  variant="danger" type="submit">
                                                Delete
                                            </Button>
                                    </div>)
                        
                            
                        };
                        })()}
                    </Form>
                    
                </Col>
                <Col>2 of 2</Col>
            </Row>
            <Row>
                <Col>1 of 3</Col>
                <Col>3 of 3</Col>
            </Row>
            </Container>
        </div>
    )
}

export default Admin
