import React,{useState,useEffect} from 'react'
import axios from "axios";
import Navb from '../components/navb';
import { Container, Form, ListGroup,Button } from 'react-bootstrap';
import {useSelector} from 'react-redux';
import { Redirect } from 'react-router-dom';

const dotenv = require("dotenv");
dotenv.config();
function Mensaje() {
    const isLogged = useSelector((store) => store.authReducer.isLogged);
    const name = useSelector((store) => store.authReducer.Name);
    const tipo= useSelector((store) => store.authReducer.tipo);
    const rut= useSelector((store) => store.authReducer.Rut);
    const [curso,setCurso] = useState([])
    const [alumnos,setAlumnos] = useState([])
    const [dest,setDest] = useState([])
    const [contenido,setContenido] = useState("")
    var crs =alumnos && alumnos.map(alumno =>{
        return alumno.rut
    })
    const instance = axios.create({
        baseURL: process.env.REACT_APP_BACKEND_URL+'/mensaje'
      });
    const handleSelectDest = (e) =>{
        if(e.target.value==="Curso"){
            setDest(crs)
        }
        else{
        setDest([e.target.value])
        }
    }
    const handleContenido = (e) =>{
        setContenido(e.target.value)
        
    }
    const handleSubmit = async (e)=>{
        try {
            await instance.post("/enviar",{
                idSender:rut,
                idReceiver:dest,
                contenido:contenido,
                

            })
        } catch (error) {
            console.log(error)
        }
        
    }
    
    useEffect(() => {
        const fetchData = async () => {
        if(tipo==="Profesor"){
          try {
            const response = await instance.post("/cosa",{
                rut:rut
            });
            setCurso(response.data[0].Curso.id);
            if(curso){
                try {
                    const resp = await instance.post("/alumnos",{
                        idCurso:curso
                    });
                    setAlumnos(resp.data)
                    
                } catch (error) {
                    
                }
            }
            
          } catch (err) {console.log(err)}
        };
        }
    
        fetchData();
      }, [curso,dest]);
      console.log(dest)
    
    return isLogged ?(
        <Container fluid>
        <Container fluid>
            <Navb name={name} tipo={tipo}></Navb>
            
        </Container>
        <Container>
        
            <Form>
            <Form.Label>Tipo Destinatario</Form.Label>
                        <Form.Control as="select" onChange={handleSelectDest}>
                            <option value={null}>Escoger Destinatario</option>
                            <option value={"Curso"}>Curso</option>
                            <option value={"Curso"}>Alumnos</option>
                            <option value={"Curso"}>Apoderados</option>
                            <option value={"Curso"}>Alumno Especifico</option>
                            <option value={"Curso"}>Apoderado Especifico</option>                      
                        </Form.Control>
            <Form.Label>Destinatario</Form.Label>
                        <Form.Control as="select" onChange={handleSelectDest}>
                            <option value={null}>Escoger Destinatario</option>
                            <option value={"Curso"}>Curso</option>
                            {alumnos && alumnos.map(alumno =>{return(<option value={alumno.rut} key={alumno.rut}>{alumno.rut}/{alumno.nombre}</option>)})}                       
                        </Form.Control>
            <Form.Label>Mensaje</Form.Label>
                <Form.Control as="textarea" rows={5} onChange={handleContenido} />
            <br/>
            <Button type="submit" onClick={handleSubmit}>Enviar</Button>
            </Form>
        </Container>
        </Container>
    ):(
        <Redirect to="/login"/>
    )
}

export default Mensaje
