import React, {useState} from 'react'
import {Button, Container,Row,Col,Form,InputGroup,DropdownButton,Dropdown } from 'react-bootstrap'
import {useDispatch } from 'react-redux';
import { login } from '../redux/actions/authActions';
import axios from "axios";
import { useHistory,Link, Redirect } from 'react-router-dom';
import { useSelector } from 'react-redux';
import '../assets/css/styles.css'
const dotenv = require("dotenv");
dotenv.config();

const instance = axios.create({
    baseURL: process.env.REACT_APP_BACKEND_URL
  });

function Login(){
    const [rut,setRut]=useState("");
    const [pass,setPass]=useState("");
    const [selected,setSelected]=useState('Usuario')
    const dispatch= useDispatch();
    const isLogged = useSelector((store) => store.authReducer.isLogged);
    const history= useHistory();

    const handleSelect = (e) =>{
        setSelected(e)
    }
    
    const handleRut = (e) => {
        setRut(e.target.value)
    }
    const handlePass = (e) => {
        setPass(e.target.value)
    }
    const handleSubmit = async (e) => {
        e.preventDefault()
        
        switch(selected){
            case "Estudiante":
                try {
                    await instance.post("auth/login/estudiante",{
                        rut :rut,
                        password: pass
                    }).then((data)=> {
                        dispatch(login(data.data.rut,data.data.nombre,selected,data.data.idCurso));
                        localStorage.setItem('token', data.data.token);
                        history.push("/home");
                    })
                    
                } catch (error) {
                    
                }
                break;
            case "Apoderado":
                try {
                    await instance.post("auth/login/apoderado",{
                        rut :rut,
                        password: pass
                    }).then((data)=> {
                        dispatch(login(data.data.rut,data.data.nombre,selected));
                        localStorage.setItem('token', data.data.token);
                        history.push("/home");
                    })
                    
                } catch (error) {
                    
                }
                break;
            case "Profesor":
                
                try {
                    await instance.post("auth/login/profesor",{
                        rut :rut,
                        password: pass
                    }).then((data)=> {
                        dispatch(login(data.data.rut,data.data.nombre,selected,null,null,data.data.jefe));
                        localStorage.setItem('token', data.data.token);
                        history.push("/home");
                    })
    
                } catch (error) {

                }
                break;
            default:
                break;
        }
    }
    return !isLogged?(
        
            
            <Container>
                
                
    
                <Form className="forma">
                    <Form.Group>
                        <Form.Label>Rut</Form.Label>
                        <InputGroup className="mb-3">
                            <Form.Control className="forma-control" onChange={handleRut} />
                            <DropdownButton
                            as={InputGroup.Append}
                            variant="outline-secondary"
                            title={selected}
                            id="input-group-dropdown-1"
                            onSelect={handleSelect}
                            >
                            <Dropdown.Item eventKey="Estudiante">Estudiante</Dropdown.Item>
                            <Dropdown.Item eventKey="Profesor">Profesor</Dropdown.Item>
                            <Dropdown.Item eventKey="Apoderado">Apoderado</Dropdown.Item>
                            </DropdownButton>
                            
                        </InputGroup>
                        
                    </Form.Group>
                    <Form.Group >
                        <Form.Label>Contrase√±a</Form.Label>
                        <Form.Control type="password" onChange={handlePass} />
                    </Form.Group>
                    <Button onClick={handleSubmit}  variant="primary" type="submit">
                        Submit
                    </Button>
                    <Link to="/">Registrarse</Link>
                </Form>
                
        </Container>

        
        
    ):(<Redirect to="/home"/>)
}

export default Login
