                                </Form.Group>
                                <br/>
                                <Button  variant="primary" type="submit" onClick={handleSubmit}>
                                    Add/Update
                                </Button>
                                <Button  variant="danger" type="submit">
                                    Delete
                                </Button>
                                </div>)
                                case 'Profesor':
                                    return(<div>
                                            <Form.Group controlId="formBasicPassword">
